pragma solidity ^0.4.4;

contract bettingContractV1 {

    address public team1bet;
    address public team2bet;

    uint public funds;
    uint public matchId;

    function bettingContractV1(uint _matchId, uint _choice) payable {
        if(_choice == 1) {
            team1bet = msg.sender;
        }
        else {
            team2bet = msg.sender;
        }

        funds = msg.value;
        matchId = _matchId;
    }

    function makeBet(uint _choice, uint _amount) payable {
        if(_choice == 1) {
            team1bet = msg.sender;
        }
        else {
            team2bet = msg.sender;
        }

        funds += msg.value;
    }

    function getFunds() returns (uint) {
        return funds;
    }

}
